using clarion.housing.console.migration.Models.Core;using FluentValidation;namespace clarion.housing.console.migration.Validation.Validators;public class HouseholdMemberValidator : AbstractValidator<HouseholdMembers>{    public HouseholdMemberValidator()    {        RuleFor(x => x.HouseholdMemberReference).NotEmpty();        RuleFor(x => x.UnitId).NotEmpty();        RuleFor(x => x.TenancyReference).NotEmpty();    }}